import React, { Component } from 'react';
import {getPosts} from "./services/fakePostService"
import PostPage from "./post-page"


class BlogComponent extends Component {
    state = {  
        pagePostNumber: 8,
        posts: [],
        postOpen: false

    }

    componentDidMount(){
        const posts = getPosts()
        this.setState({
            posts
        })

    }

    getImageUrl = (post) => {
        return post.imageUrl
    }

    getEightPost = () => {
        const startIndex = 0;
        const posts = this.state.posts 
        const eightPost = posts.slice(startIndex, (startIndex + 8))
        return eightPost;

    }

    openPostPage = (post) => {
        return(<PostPage post= {post} />)
    }

    render() { 
        const posts = this.getEightPost()
        console.log(posts);

        return (  
            <div>
                <div className="container">

                    <div className="title-container"> <h1 className="blog-title"> BLOG</h1></div>
                    <div className="blog-content">
                        <div className="content-row-eight"> 
                            {posts.map( post=> (  
                                <div key={post._id} className="content"> 
                                    <div className="content-image" style={{  }}>
                                        <img src={ this.getImageUrl(post) } alt=""/>
                                    </div>
                                    <div className="paragraph-container" > <p  className="content-paragraph">{post.post}</p> </div>
                                    <div className="link-content" > <a onClick={(post) => this.openPostPage(post)}  className="read-more">Devamını Oku</a> </div>    
                                </div> 

                            ))}    
                        </div>
                    </div>
                </div>
            </div>
    );}
}

export default BlogComponent;
